import React, { useState, useEffect } from 'react';
import { Card, Button, Table, Modal, Form, Row, Col, Badge, Pagination } from 'react-bootstrap';
import { toast } from 'react-toastify';
import loanService from '../services/loanService';
import assetService from '../services/assetService';
import LoadingSpinner from '../components/LoadingSpinner';
import { useAuth } from '../contexts/AuthContext';
import moment from 'moment';

const LoanManagement = () => {
  const [loans, setLoans] = useState([]);
  const [assets, setAssets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [showReturnModal, setShowReturnModal] = useState(false);
  const [selectedLoan, setSelectedLoan] = useState(null);
  const [pagination, setPagination] = useState({});
  const [filters, setFilters] = useState({
    page: 1,
    limit: 10,
    search: '',
    status: ''
  });

  const [loanForm, setLoanForm] = useState({
    assetId: '',
    borrowerName: '',
    borrowerPhone: '',
    purpose: '',
    returnDate: ''
  });

  const [returnForm, setReturnForm] = useState({
    notes: ''
  });

  const { user } = useAuth();

  useEffect(() => {
    loadLoans();
    loadAssets();
  }, [filters]);

  const loadLoans = async () => {
    try {
      const response = await loanService.getLoans(filters);
      setLoans(response.loans);
      setPagination(response.pagination);
    } catch (error) {
      console.error('Error loading loans:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadAssets = async () => {
    try {
      const response = await assetService.getAssets({ 
        isAvailable: true, 
        isActive: true,
        limit: 100
      });
      setAssets(response.assets);
    } catch (error) {
      console.error('Error loading assets:', error);
    }
  };

  const handleShowModal = () => {
    setLoanForm({
      assetId: '',
      borrowerName: '',
      borrowerPhone: '',
      purpose: '',
      returnDate: ''
    });
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setShowReturnModal(false);
    setSelectedLoan(null);
  };

  const handleLoanSubmit = async (e) => {
    e.preventDefault();
    
    try {
      await loanService.createLoan(loanForm);
      toast.success('Peminjaman berhasil dibuat!');
      handleCloseModal();
      loadLoans();
      loadAssets(); // Refresh available assets
    } catch (error) {
      console.error('Error creating loan:', error);
    }
  };

  const handleReturn = (loan) => {
    setSelectedLoan(loan);
    setReturnForm({ notes: '' });
    setShowReturnModal(true);
  };

  const handleReturnSubmit = async (e) => {
    e.preventDefault();
    
    try {
      await loanService.returnAsset(selectedLoan.id, returnForm.notes);
      toast.success('Asset berhasil dikembalikan!');
      handleCloseModal();
      loadLoans();
      loadAssets(); // Refresh available assets
    } catch (error) {
      console.error('Error returning asset:', error);
    }
  };

  const getStatusBadge = (status) => {
    switch (status) {
      case 'BORROWED':
        return <Badge bg="warning" className="badge-borrowed">Dipinjam</Badge>;
      case 'RETURNED':
        return <Badge bg="success" className="badge-returned">Dikembalikan</Badge>;
      case 'OVERDUE':
        return <Badge bg="danger" className="badge-overdue">Terlambat</Badge>;
      default:
        return <Badge bg="secondary">{status}</Badge>;
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: 1 // Reset to first page when filtering
    }));
  };

  const handlePageChange = (page) => {
    setFilters(prev => ({ ...prev, page }));
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h2>Manajemen Peminjaman</h2>
          <p className="text-muted mb-0">Kelola peminjaman kendaraan dan kunci ruangan</p>
        </div>
        <Button variant="primary" onClick={handleShowModal}>
          <i className="fas fa-plus me-2"></i>
          Buat Peminjaman
        </Button>
      </div>

      <Card>
        <Card.Header>
          <Row className="align-items-center">
            <Col md={4}>
              <Form.Control
                type="text"
                placeholder="Cari peminjam, asset, atau kode..."
                value={filters.search}
                onChange={(e) => handleFilterChange('search', e.target.value)}
              />
            </Col>
            <Col md={3}>
              <Form.Select
                value={filters.status}
                onChange={(e) => handleFilterChange('status', e.target.value)}
              >
                <option value="">Semua Status</option>
                <option value="BORROWED">Dipinjam</option>
                <option value="RETURNED">Dikembalikan</option>
                <option value="OVERDUE">Terlambat</option>
              </Form.Select>
            </Col>
            <Col md={5} className="text-end">
              <small className="text-muted">
                Total: {pagination.total || 0} peminjaman
              </small>
            </Col>
          </Row>
        </Card.Header>

        <Card.Body className="p-0">
          {loans.length > 0 ? (
            <div className="table-responsive">
              <Table className="mb-0">
                <thead>
                  <tr>
                    <th>Asset</th>
                    <th>Peminjam</th>
                    <th>Tanggal Pinjam</th>
                    <th>Tanggal Kembali</th>
                    <th>Status</th>
                    <th>Petugas</th>
                    <th>Aksi</th>
                  </tr>
                </thead>
                <tbody>
                  {loans.map((loan) => (
                    <tr key={loan.id}>
                      <td>
                        <div>
                          <strong>{loan.asset.name}</strong>
                          <br />
                          <small className="text-muted">{loan.asset.code}</small>
                        </div>
                      </td>
                      <td>
                        <div>
                          <strong>{loan.borrowerName}</strong>
                          {loan.borrowerPhone && (
                            <>
                              <br />
                              <small className="text-muted">{loan.borrowerPhone}</small>
                            </>
                          )}
                        </div>
                      </td>
                      <td>
                        {moment(loan.loanDate).format('DD/MM/YYYY HH:mm')}
                      </td>
                      <td>
                        {loan.returnDate 
                          ? moment(loan.returnDate).format('DD/MM/YYYY HH:mm')
                          : '-'
                        }
                        {loan.actualReturnDate && (
                          <>
                            <br />
                            <small className="text-success">
                              Aktual: {moment(loan.actualReturnDate).format('DD/MM/YYYY HH:mm')}
                            </small>
                          </>
                        )}
                      </td>
                      <td>
                        {getStatusBadge(loan.status)}
                      </td>
                      <td>
                        <small>{loan.user.fullName}</small>
                      </td>
                      <td>
                        {loan.status === 'BORROWED' && (
                          <Button
                            variant="success"
                            size="sm"
                            onClick={() => handleReturn(loan)}
                          >
                            <i className="fas fa-check me-1"></i>
                            Kembalikan
                          </Button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </div>
          ) : (
            <div className="empty-state">
              <i className="fas fa-handshake"></i>
              <p>Belum ada data peminjaman</p>
            </div>
          )}
        </Card.Body>

        {pagination.totalPages > 1 && (
          <Card.Footer>
            <Pagination className="justify-content-center mb-0">
              <Pagination.Prev
                disabled={pagination.page <= 1}
                onClick={() => handlePageChange(pagination.page - 1)}
              />
              {[...Array(pagination.totalPages)].map((_, index) => (
                <Pagination.Item
                  key={index + 1}
                  active={index + 1 === pagination.page}
                  onClick={() => handlePageChange(index + 1)}
                >
                  {index + 1}
                </Pagination.Item>
              ))}
              <Pagination.Next
                disabled={pagination.page >= pagination.totalPages}
                onClick={() => handlePageChange(pagination.page + 1)}
              />
            </Pagination>
          </Card.Footer>
        )}
      </Card>

      {/* Create Loan Modal */}
      <Modal show={showModal} onHide={handleCloseModal} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Buat Peminjaman Baru</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleLoanSubmit}>
          <Modal.Body>
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Asset *</Form.Label>
                  <Form.Select
                    value={loanForm.assetId}
                    onChange={(e) => setLoanForm({...loanForm, assetId: e.target.value})}
                    required
                  >
                    <option value="">Pilih Asset</option>
                    {assets.map((asset) => (
                      <option key={asset.id} value={asset.id}>
                        {asset.name} - {asset.code} ({asset.category.name})
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>

              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Nama Peminjam *</Form.Label>
                  <Form.Control
                    type="text"
                    value={loanForm.borrowerName}
                    onChange={(e) => setLoanForm({...loanForm, borrowerName: e.target.value})}
                    required
                  />
                </Form.Group>
              </Col>

              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>No. Telepon</Form.Label>
                  <Form.Control
                    type="tel"
                    value={loanForm.borrowerPhone}
                    onChange={(e) => setLoanForm({...loanForm, borrowerPhone: e.target.value})}
                  />
                </Form.Group>
              </Col>

              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Tanggal Kembali</Form.Label>
                  <Form.Control
                    type="datetime-local"
                    value={loanForm.returnDate}
                    onChange={(e) => setLoanForm({...loanForm, returnDate: e.target.value})}
                    min={new Date().toISOString().slice(0, 16)}
                  />
                </Form.Group>
              </Col>

              <Col md={12}>
                <Form.Group className="mb-3">
                  <Form.Label>Keperluan</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    value={loanForm.purpose}
                    onChange={(e) => setLoanForm({...loanForm, purpose: e.target.value})}
                    placeholder="Deskripsikan keperluan peminjaman..."
                  />
                </Form.Group>
              </Col>
            </Row>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleCloseModal}>
              Batal
            </Button>
            <Button variant="primary" type="submit">
              <i className="fas fa-save me-2"></i>
              Simpan Peminjaman
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>

      {/* Return Asset Modal */}
      <Modal show={showReturnModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Kembalikan Asset</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleReturnSubmit}>
          <Modal.Body>
            {selectedLoan && (
              <div className="mb-3">
                <strong>Asset:</strong> {selectedLoan.asset.name} - {selectedLoan.asset.code}
                <br />
                <strong>Peminjam:</strong> {selectedLoan.borrowerName}
                <br />
                <strong>Tanggal Pinjam:</strong> {moment(selectedLoan.loanDate).format('DD/MM/YYYY HH:mm')}
              </div>
            )}
            
            <Form.Group className="mb-3">
              <Form.Label>Catatan Pengembalian</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={returnForm.notes}
                onChange={(e) => setReturnForm({...returnForm, notes: e.target.value})}
                placeholder="Kondisi asset, kerusakan, atau catatan lainnya..."
              />
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleCloseModal}>
              Batal
            </Button>
            <Button variant="success" type="submit">
              <i className="fas fa-check me-2"></i>
              Konfirmasi Pengembalian
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </div>
  );
};

export default LoanManagement;
