import React from 'react';
import { Nav } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Sidebar = ({ isOpen, onToggle }) => {
  const location = useLocation();
  const { isAdmin } = useAuth();

  const menuItems = [
    {
      path: '/dashboard',
      icon: 'fas fa-tachometer-alt',
      label: 'Dashboard',
      roles: ['ADMIN', 'SATPAM']
    },
    {
      path: '/loans',
      icon: 'fas fa-handshake',
      label: 'Peminjaman',
      roles: ['ADMIN', 'SATPAM']
    },
    {
      path: '/history',
      icon: 'fas fa-history',
      label: 'Riwayat Peminjaman',
      roles: ['ADMIN', 'SATPAM']
    },
    {
      path: '/assets',
      icon: 'fas fa-car',
      label: 'Kelola Asset',
      roles: ['ADMIN']
    },
    {
      path: '/categories',
      icon: 'fas fa-tags',
      label: 'Kelola Kategori',
      roles: ['ADMIN']
    },
    {
      path: '/users',
      icon: 'fas fa-users',
      label: 'Kelola User',
      roles: ['ADMIN']
    }
  ];

  const filteredMenuItems = menuItems.filter(item => 
    isAdmin ? true : item.roles.includes('SATPAM')
  );

  return (
    <>
      {/* Mobile overlay */}
      {isOpen && (
        <div 
          className="position-fixed top-0 start-0 w-100 h-100 bg-dark bg-opacity-50 d-md-none"
          style={{ zIndex: 1040 }}
          onClick={onToggle}
        />
      )}
      
      <div className={`sidebar ${isOpen ? 'show' : ''} d-md-block col-md-3 col-lg-2`}>
        <div className="p-3">
          <div className="text-center mb-4">
            <div className="bg-white rounded-circle d-inline-flex align-items-center justify-content-center" 
                 style={{ width: '60px', height: '60px' }}>
              <img src="./images/tlkm.png" alt="Logo-Telkom" style={{ width: '100%', height: '100%' }} />
            </div>
            <h6 className="text-white mt-2 mb-0">Telkom Bojonegoro</h6>
          </div>
          
          <Nav className="flex-column">
            {filteredMenuItems.map((item, index) => (
              <Nav.Link
                key={index}
                as={Link}
                to={item.path}
                className={location.pathname === item.path ? 'active' : ''}
                onClick={() => isOpen && onToggle()}
              >
                <i className={item.icon}></i>
                {item.label}
              </Nav.Link>
            ))}
          </Nav>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
